import { GET_ERRORS, CLEAR_ERRORS } from '../actions/types/constTypes'

    const initState = { 
        msg: {},
        status: null,
        id: null         

    }

    export default (state = initState, action) => {

        switch(action.type){

        case GET_ERRORS:
            return {
                msg: action.payload.msg,
                status: action.payload.status,
                id: action.payload.id
            };

        case CLEAR_ERRORS:
            return {
                msg: {},
                status: null,
                id: null
            };

        default: 
            return state 
        }      
    }
